sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    // construct GlideRecord query to find all Needit Task records with due date in the past
    // for the same assignee. the records can not be in state closed.
    var rightNow = new GlideDateTime();

    var overdueNITasks = new GlideRecord('x_58872_needit_needit_task');

    overdueNITasks.addQuery('due_date', '<=', rightNow);
    overdueNITasks.addQuery('state', '<', 3);
    overdueNITasks.addQuery('assigned_to', current.assigned_to);

    overdueNITasks.query();

    // print message to template if there are more open tasks
    var taskCount = overdueNITasks.getRowCount();
    if (taskCount > 1)
        template.print('In addition to this overdue NeedIt task, you have ' +
            (taskCount - 1) + ' more overdue NeedIt tasks!');


})(current, template, email, email_action, event);
