sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 * @param {ApiHelpers} params.helpers
 */
function handler({api, event, helpers, imports}) 
{
    const sListQuery = api.state.listQuery;
    const sListTitle = api.state.listTitle;

    const aListQuery = sListQuery.split('^u_request_type=');
    const aListTitle = sListTitle.split(' - ');

    let sPassedQuery = event.payload.heListQuery;
    let sPassedTitle = event.payload.heListTitle;

    // check to see if a request type is passed in the payload.
    if (sPassedQuery == 'legal' || sPassedQuery == 'hr' || sPassedQuery == 'facilities')
    {
        api.setState('listQuery', aListQuery[0] + '^u_request_type=' + sPassedQuery);
        api.setState('listTitle', aListTitle[0] + ' - ' + sPassedQuery);
        api.setState('reqType', sPassedQuery);
    }
    else if (sPassedQuery == 'all')
    {
        api.setState('listQuery', aListQuery[0]);
        api.setState('listTitle', aListTitle[0]);
        api.setState('reqType', sPassedQuery);
    }
    else
    {
        // else just pass the values to the event handler. This branch will execute when filter buttons are clicked
        api.setState('listQuery', sPassedQuery);
        api.setState('listTitle', sPassedTitle);

        if (api.state.reqType != 'all')
        {
            sPassedQuery = sPassedQuery + '^u_request_type=' + api.state.reqType;
            sPassedTitle = sPassedTitle + ' - ' + api.state.reqType;
        }
    }
}
